import React from "react";
import Layout from "../../Common/Component/Layout";
import { Link } from "react-router-dom";
import { injectIntl, FormattedMessage } from "react-intl";
import SearchInput from "./Component/SearchInput";
import * as Constant from "../../Common/Util/Constant";
import GameCard from "../../Common/Component/Gamecard";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./index.scss";

import playStoreIcon from "../../Image/google-play-badge@2x.png";

function View(props) {
  const {
    recommendProjects,
    weeklyProjects,
    editorsProjects,
    viewProjects,
    newProjects,
    searchResults,
    searchedValue,
    searchValue,
    pageMode,
    isGame,
    handleModeChange,
    handleSearchChange,
    handleSearchSubmit,
    handleMoreWeeklyViewProject,
    intl
  } = props;

  const recommandGameSettings = {
    dots: true,
    arrows: true,
    infinite: true,
    draggable: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
    responsive: [
      {
        breakpoint: 1080,
        settings: {
          dots: false,
          arrow: false,
          slidesToShow: 2.02,
          slidesToScroll: 2,
          draggable: true
        }
      }
    ]
  };

  const Week__Like__Game = (item, i) => {
    return (
      <Link to={`?pId=${item.pId}`} className="Game__Box" key={i}>
        <div className="Game__Box__Image__Wrapper">
          <img
            src={
              (item.hasOwnProperty("project") && item.project.icon) ||
              (item.hasOwnProperty("published") && item.published.icon) ||
              item.icon
            }
            alt=""
            className="Game__Box__Image"
          />
        </div>
        <div className="Game__Info">
          <img
            className="Game__User__Icon"
            src={
              (item.hasOwnProperty("project") && item.project.user.icon) ||
              (item.hasOwnProperty("published") && item.published.user.icon) ||
              item.user.icon
            }
            alt=""
          />
          <p className="Game__Box__Title">
            {(item.hasOwnProperty("project") && item.project.name) ||
              (item.hasOwnProperty("published") && item.published.name) ||
              item.name}
          </p>
          <p className="Game__Box__Maker">
            {(item.hasOwnProperty("project") && item.project.user.name) ||
              (item.hasOwnProperty("published") && item.published.user.name) ||
              item.user.name}
          </p>
          <div className="Game__Etc">
            <span className="like">
              {(item.hasOwnProperty("project") && item.project.likeCount) ||
                (item.hasOwnProperty("published") &&
                  item.published.likeCount) ||
                item.likeCount ||
                0}
            </span>
            <span className="view">
              {(item.hasOwnProperty("project") && item.project.viewCount) ||
                (item.hasOwnProperty("published") &&
                  item.published.viewCount) ||
                item.viewCount ||
                0}
            </span>
            <span className="comment">
              {(item.hasOwnProperty("project") && item.project.commentCount) ||
                (item.hasOwnProperty("published") &&
                  item.published.commentCount) ||
                item.commentCount ||
                0}
            </span>
          </div>
        </div>
      </Link>
    );
  };

  const GameBox = (item, i) => {
    return (
      <Link to={`?pId=${item.pId}`} className="Game__Box" key={i}>
        <div className="Game__Box__Image__Wrapper">
          <img src={item.icon} alt="" className="Game__Box__Image" />
        </div>
        <div className="Game__Info">
          <img className="Game__User__Icon" src={item.user.icon} alt="" />
          <p className="Game__Box__Title">{item.name}</p>
          <p className="Game__Box__Maker">{item.user.name}</p>
          <div className="Game__Etc">
            <span className="like">{item.likeCount}</span>
            <span className="view">{item.viewCount}</span>
            <span className="comment">{item.commentCount}</span>
          </div>
        </div>
      </Link>
    );
  };

  const View_More_Btn = type => {
    return (
      <div className="Btn__ViewMore" onClick={handleMoreWeeklyViewProject}>
        <span className="Btn_ViewMore_Text">
          <FormattedMessage id="ID_GAME_VIEW_MORE" />
        </span>
      </div>
    );
  };

  return (
    <Layout isGame={isGame}>
      <div className="Page--Game">
        {pageMode === Constant.GAMEPAGE_MODE.SEARCH && (
          <div className="SearchResult">
            {/* Search */}

            <SearchInput
              containerName={"WizAppSearch"}
              value={searchValue}
              onChange={handleSearchChange}
              onSubmit={handleSearchSubmit}
            />

            <div className="SearchSection">
              <h1 className="SearchTitle">
                <FormattedMessage id="ID_SEARCH_RESULT" />
              </h1>
              <h1 className="SearchKeyword Search__highlight">
                {searchedValue}
              </h1>
              <button
                className="BackButton"
                onClick={() => {
                  handleModeChange(Constant.GAMEPAGE_MODE.NORMAL);
                }}
              >
                <p className="Back" />
              </button>
            </div>

            <div className="SearchGameItems">
              {searchResults.length > 0 ? (
                searchResults.map((item, idx) => {
                  return (
                    <div className="GameItem" key={idx}>
                      <GameCard game={item} />
                    </div>
                  );
                })
              ) : (
                <div>
                  <div className="SearchGameItems__NoItem__Text">검색 결과가 없습니다.</div>
                </div>
              )}
            </div>
          </div>
        )}

        {pageMode === Constant.GAMEPAGE_MODE.MORE && (
          <div className="SearchResult">
            <div className="SearchGameItems" />
          </div>
        )}

        {pageMode === Constant.GAMEPAGE_MODE.NORMAL && (
          <>
            {/* Search */}
            <div className="Search">
              <SearchInput
                containerName={"WizAppSearch"}
                value={searchValue}
                onChange={handleSearchChange}
                onSubmit={handleSearchSubmit}
              />
            </div>

            <div className="Game__Wizlab__Recommand__Game">
              <div className="Game__Inner">
                <div className="Game__Title">
                  <FormattedMessage id="ID_GAME_RECOMMEND_TITLE" />
                </div>
                <div className="Game__List">
                  {recommendProjects.length > 0 ? (
                    <Slider {...recommandGameSettings}>
                      {recommendProjects.map((item, i) => {
                        return (
                          <Link
                            to={`?pId=${item.pId}`}
                            className="Game__Box"
                            key={i}
                          >
                            <div className="Game__Box__Image__Wrapper">
                              <div
                                className="Game__Box__Image"
                                style={{
                                  background: `url(${
                                    item.icon ? item.icon : item.project.icon
                                  }) no-repeat center/cover`
                                }}
                              />
                            </div>
                            <div className="Game__Info">
                              <img
                                className="Game__Box__UserIcon"
                                src={item.project.user.icon}
                                alt=""
                              />
                              <p className="Game__Box__Title">
                                {item.project.name}
                              </p>
                              <p className="Game__Box__Maker">
                                {item.project.user.name}
                              </p>
                              <div className="Game__Etc">
                                <span className="like">
                                  {item.project.likeCount}
                                </span>
                                <span className="view">
                                  {item.project.viewCount}
                                </span>
                                <span className="comment">
                                  {item.project.commentCount}
                                </span>
                              </div>
                            </div>
                          </Link>
                        );
                      })}
                    </Slider>
                  ) : (
                    <p className="defaultText">위즈랩 추천 게임이 없습니다.</p>
                  )}
                </div>
              </div>
            </div>

            <div className="Game__Week__Like__Game">
              <div className="Game__Inner">
                <div className="Game__Title">
                  <FormattedMessage id="ID_GAME_WEEKLY_LIKE_TITLE" />
                </div>
                <div className="Game__List">
                  {weeklyProjects.map((item, i) => {
                    return Week__Like__Game(item, i);
                  })}

                  {weeklyProjects.length < 6 && View_More_Btn("weekly")}
                </div>
              </div>
            </div>

            <div className="Game__Editor__Recommand__Game">
              <div className="Game__Inner">
                <p className="Game__Editor__Recommand__Game__Title">
                  <FormattedMessage id="ID_GAME_EDITOR_TITLE" />
                  <Link
                    to={"/editorRecommend"}
                    className="Game__Editor__Recommand__Game__ViewMore"
                  >
                    {intl.formatMessage({ id: "ID_GAME_EDITOR_MORE" })}
                  </Link>
                </p>
                <div className="Game__Editor__Recommand__Game__Wrapper">
                  {editorsProjects.map((game, i) => {
                    return (
                      <div
                        className="Game__Editor__Recommand__Game__Box"
                        key={i}
                      >
                        <Link
                          to={`?pId=${game.pId}`}
                          className="Game__Editor__Recommand__Game__Box"
                          key={i}
                        >
                          <p className="Game__Editor__Recommand__Game__Image_Wrapper">
                            <img src={game.icon} alt="" className="game_icon" />
                          </p>
                          <div className="Game__Editor__Recommand__Game__Info_Wrapper">
                            <p className="Game__Title">{game.project.name}</p>
                            <p className="Game__Maker">
                              {game.project.user.name}
                            </p>
                          </div>
                          <p className="Game__Info">{game.description}</p>
                        </Link>
                        {game.playStoreURL && (
                          <a
                            href={game.playStoreURL}
                            className="playStoreIcon"
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            <img src={playStoreIcon} alt="playstore icon" />
                          </a>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>

            <div className="Game__Wizlab__RealTime__Game">
              <div className="Game__Inner">
                <div className="Game__Title">
                  <FormattedMessage id="ID_WIZAPP_VIEWS_TITLE" />
                </div>

                <div className="Game__List">
                  <Slider {...recommandGameSettings}>
                    {viewProjects.length > 0 ? (
                      viewProjects.map((item, i) => {
                        return (
                          <Link
                            to={`?pId=${item.pId}`}
                            className="Game__Box"
                            key={i}
                          >
                            <div className="Game__Box__Image__Wrapper">
                              <div
                                className="Game__Box__Image"
                                style={{
                                  background: `url(${item.icon}) no-repeat center/cover`
                                }}
                              />
                            </div>
                            <div className="Game__Info">
                              <img
                                className="Game__Box__UserIcon"
                                src={item.user.icon}
                                alt=""
                              />
                              <p className="Game__Box__Title">{item.name}</p>
                              <p className="Game__Box__Maker">
                                {item.user.name}
                              </p>
                              <div className="Game__Etc">
                                <span className="like">{item.likeCount}</span>
                                <span className="view">{item.viewCount}</span>
                                <span className="comment">
                                  {item.commentCount}
                                </span>
                              </div>
                            </div>
                          </Link>
                        );
                      })
                    ) : (
                      <p>실시간 플레이 게임이 없습니다.</p>
                    )}
                  </Slider>
                </div>
              </div>
            </div>

            <div className="Game__News__Game">
              <div className="Game__Inner">
                <div className="Game__Title">
                  <FormattedMessage id="ID_GAME_NEWS_GAME_TITLE" />
                </div>
                <div className="Game__List">
                  {newProjects.map((item, i) => GameBox(item, i))}
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </Layout>
  );
}
export default injectIntl(View);
